import numpy as np
arr = np.arange(64)
darr = arr.reshape(8,-1)
darr

darr[:3,:3] #원하는 부분을 추출하는 방법

kernel = np.array([[0,1,0],[1,0,0],[0,0,1]])
kernel

feature = np.zeros((6,6))
for i in range(6):
  for j in range(6):
    da = np.dot(darr[i:i+3,j:j+3],kernel)
    feature[i,j] = da.sum()
print(feature)

src = np.array([[0,1,2],[8,9,10],[16,17,18]])
print((np.dot(src,kernel)).sum())

